using System.Collections; using System.Collections.Generic; using UnityEngine; using UnityEngine.SceneManagement;   public class PlayCreditsOrRestart : Action {      public string lvl;      // Start is called before the first frame update     void Start()     {              }      // Update is called once per frame     void Update()     {              }      public override void Do()     {         if (lvl == null)         {             Debug.Log("Level parameter was not entered. Enter value to public variable");             return;         }         SceneManager.LoadScene(lvl);     } } 